function showClapper(){

    var index = $("a.nav-link.active").hash;
    console.log($("a.nav-link.active").id);

    if(index != "#header"){
        document.querySelector('.clapper').style.display = "none";
        document.querySelector('.creditsHome').style.display = "none";
        window.location.hash = index;
    }else{
        document.querySelector('.clapper').style.display = "block";
        document.querySelector('.creditsHome').style.display = "block";
        window.location.hash = "";
        sectionZ();
    }
    
}


